#!/bin/bash
gcc -I../../../cgic -I../../../libpvn/src -O3 -march=native -fPIC -fexceptions -fasynchronous-unwind-tables -fno-omit-frame-pointer -fvect-cost-model=unlimited -ffp-contract=fast -fno-math-errno -c jsvdt.c
gfortran -DUSE_IEEE_INTRINSIC=IEEE_FMA -O3 -march=native -fPIC -fexceptions -fasynchronous-unwind-tables -fno-omit-frame-pointer -fvect-cost-model=unlimited -ffp-contract=fast -fno-math-errno -ffree-line-length-none -fprotect-parens -frecursive -fstack-arrays jsvdt.F90 jsvdt.o -o jsvdt.cgi -rdynamic -static -s -L../../src -ljaevd -ljaev2 -L../../../cgic -lcgic -L../../../libpvn/src -lpvn -lm
